CV_Completo/cv_completo.js:
function descargarCV() {
  const contenido = `Nombre: Nahuel Morales\nUbicaci√≥n: Mendoza, Argentina\nEspecialidades: Frontend tipo VSCode, backend modular, depuraci√≥n visual\nContacto: WhatsApp 2622549642\nEmail: moralesvallenahuel@hotmail.com`;
  const blob = new Blob([contenido], { type: 'text/plain' });
  const link = document.createElement('a');
  link.href = URL.createObjectURL(blob);
  link.download = 'CV_Nahuel_Morales.txt';
  link.click();
}
